package PatternMaching;

public class P20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n=5;
for(int i=1;i<=n;i++) {
	int k=1;
	for(int j=1;j<=i;j++) {
		System.out.print(k+" ");
		k++;
	}
	{
		System.out.print(" ");
	}
	System.out.println();
}
	}

}

