package PatternMaching;

public class P5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n=5;
for(int i=n;i>=1;i--) {
	int k=n;
	for(int j=1;j<=n;j++) {
		System.out.print(k+" ");
		k--;
	}
	System.out.println();
}
	}

}
//
//5 4 3 2 1 
//5 4 3 2 1 
//5 4 3 2 1 
//5 4 3 2 1 
//5 4 3 2 1 
